    private void btnAdcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAdcActionPerformed
        Produto p = new Produto();
 if(tNome.getText().equals("")||String.valueOf(tPco).equals("")||String.valueOf(tQnt).equals("")){
        JOptionPane.showMessageDialog(null,"Preencha todos os campos");
        System.exit(10);
        }
 else
        numreg++;
         tCod.setText(String.valueOf(numreg));
        p.setcodprod(numreg);
        p.setNome(tNome.getText());
        p.setTam(String.valueOf(cbxTam.getSelectedItem()));
        p.setPreco(Double.parseDouble(tPco.getText()));
        p.setQuant(Integer.parseInt(tQnt.getText()));
        c.adciona(p);
        System.out.println(tNome.getText().substring(0, 3));
    }//GEN-LAST:event_btnAdcActionPerformed

    private void btnPsqnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPsqnActionPerformed
 int i=c.PesqNome(JOptionPane.showInputDialog(null,"Digite o nome do produto"));
      
       tCod.setEnabled(true);
       tCod.setText(String.valueOf(c.getlistProd().get(i).getcodprod()));
       tNome.setText(c.getlistProd().get(i).getNome());
       tPco.setText(String.valueOf(c.getlistProd().get(i).getPreco()));
       tQnt.setText(String.valueOf(c.getlistProd().get(i).getQuant()));
       cbxTam.setSelectedItem(c.getlistProd().get(i).getTam());
       tCod.setEnabled(false);
    }//GEN-LAST:event_btnPsqnActionPerformed

    private void btnPsqcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPsqcActionPerformed
 int i=c.consporCod(Integer.parseInt(JOptionPane.showInputDialog(null,"Digite o código do produto")));
 
       tCod.setEnabled(true);
       tCod.setText(String.valueOf(c.getlistProd().get(i).getcodprod()));
       tNome.setText(c.getlistProd().get(i).getNome());
       tPco.setText(String.valueOf(c.getlistProd().get(i).getPreco()));
       tQnt.setText(String.valueOf(c.getlistProd().get(i).getQuant()));
       cbxTam.setSelectedItem(c.getlistProd().get(i).getTam());
       tCod.setEnabled(false);
    }//GEN-LAST:event_btnPsqcActionPerformed

