    private void mbRemoverActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_mbRemoverActionPerformed
     int i=c.remov(Integer.parseInt(JOptionPane.showInputDialog(null,"Informe o código do produto")));
     if(i==0){
         JOptionPane.showMessageDialog(null,"Item removido");
     }
     
     else{
         JOptionPane.showMessageDialog(null,"Erro ao remover do registro");
     }
    }//GEN-LAST:event_mbRemoverActionPerformed

    private void mbSaidaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_mbSaidaActionPerformed
      int resp=JOptionPane.showConfirmDialog(null, "Deseja sair?");
      if (resp==0){
          JOptionPane.showMessageDialog(null,"Tchau Tchau");
          System.exit(0);
      }
      else{
          System.out.println(resp);
      }
    }//GEN-LAST:event_mbSaidaActionPerformed

    private void ativado(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_ativado
     tCod.setEnabled(false);
        tNome.requestFocus();
    }//GEN-LAST:event_ativado

